<?php//------------------- Add section footer// Add Footer section.$wp_customize->add_section(    'footer',    array(        'title'    => __( 'Footer', '' ),        'priority' => 30,    ));// Add color setting.$wp_customize->add_setting(    'footer_color',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'footer',        'default'    => '#ffffff',    ));// Add color control.$wp_customize->add_control(    new WP_Customize_Color_Control(        $wp_customize,        'footer_color',        array(            'label'    => __( 'Color first', 'restaurant' ),            'section'  => 'footer',            'settings' => 'footer_color',        )    ));// Add background setting.$wp_customize->add_setting(    'footer_background',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'footer',        'default'    => '#1d2a49',    ));// Add background control.$wp_customize->add_control(    new WP_Customize_Color_Control(        $wp_customize,        'footer_background',        array(            'label'    => __( 'Background Color first', 'restaurant' ),            'section'  => 'footer',            'settings' => 'footer_background',        )    ));//---------------------------------------- Background Image Footer// Add Background Image footer section$wp_customize->add_setting(    'page_footer_background',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',    ));// Add Background Image footer control.$wp_customize->add_control(    new WP_Customize_Image_Control(        $wp_customize,        'page_footer_background',        array(            'label'    => __( 'Background Footer', 'restaurant' ),            'section'  => 'footer',            'settings' => 'page_footer_background',        )    ));