<?php// Add shop-single section.$wp_customize->add_section(    'shop_single',    array(        'title'    => __( 'Shop single', 'restaurant' ),        'priority' => 30,    ));// Add sidebar page single product.$wp_customize->add_setting(    'shop_single_sidebar',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'shop_single',        'default'    => 'full',    ));// Add sidebar page single product control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'shop_single_sidebar',        array(            'label'    => __( 'Sidebar', 'restaurant' ),            'section'  => 'shop_single',            'settings' => 'shop_single_sidebar',            'type'     => 'select',            'choices'   => array(                'left' => __( 'Left', 'restaurant' ),                'right' => __( 'Right', 'restaurant' ),                'full' => __( 'No Sidebar', 'restaurant' ),            )        )    ));// Add shop-single title setting.$wp_customize->add_setting(    'shop_single_related_column',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'shop_single',        'default'    => 4,    ));// Add shop-single title control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'shop_single_related_column',        array(            'label'    => __( 'Shop Single Related Column', 'restaurant' ),            'section'  => 'shop_single',            'settings' => 'shop_single_related_column',            'type'     => 'select',            'choices'   => array(                1 => 1,                2 => 2,                3 => 3,                4 => 4,            )        )    ));