<?php//------------------- Add section footer// Add Shop section.$wp_customize->add_section(    'shop',    array(        'title'    => __( 'Shop', '' ),        'priority' => 30,    ));// Add Shop title setting.    $wp_customize->add_setting(        'shop_column',        array(            'type'       => 'option', // 'option' || 'theme_mod            'capability' => 'edit_theme_options',            'section'    => 'shop',            'default'    => 1,        )    );// Add Shop title control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'shop_column',        array(            'label'    => __( 'Shop Column', 'restaurant' ),            'section'  => 'shop',            'settings' => 'shop_column',            'type'     => 'select',            'choices'   => array(                1 => 1,                2 => 2,                3 => 3,                4 => 4,            )        )    ));