<?php// Add Shop section.$wp_customize->add_section(    'shop',    array(        'title'    => __( 'Shop', '' ),        'priority' => 30,    ));// Add sidebar page single product.$wp_customize->add_setting(    'shop_sidebar',    array(        'type'       => 'option', // 'option' || 'theme_mod        'capability' => 'edit_theme_options',        'section'    => 'shop',        'default'    => 'left',    ));// Add sidebar page single product control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'shop_sidebar',        array(            'label'    => __( 'Sidebar', 'restaurant' ),            'section'  => 'shop',            'settings' => 'shop_sidebar',            'type'     => 'select',            'choices'   => array(                'left' => __( 'Left', 'restaurant' ),                'right' => __( 'Right', 'restaurant' ),                'full' => __( 'No Sidebar', 'restaurant' ),            )        )    ));// Add Shop title setting.    $wp_customize->add_setting(        'shop_column',        array(            'type'       => 'option', // 'option' || 'theme_mod            'capability' => 'edit_theme_options',            'section'    => 'shop',            'default'    => 1,        )    );// Add Shop title control.$wp_customize->add_control(    new WP_Customize_Control(        $wp_customize,        'shop_column',        array(            'label'    => __( 'Shop Column', 'restaurant' ),            'section'  => 'shop',            'settings' => 'shop_column',            'type'     => 'select',            'choices'   => array(                1 => 1,                2 => 2,                3 => 3,                4 => 4,            )        )    ));